mapper.select:
select count(*)
from MEMBER
where MEMBERID = #{memberId}

select count(*)
from MEMBER
where NICKNAME = #{nickName}

select count(*)
from MEMBER
where EMAIL = #{email}

SELECT MEMBERID
from MEMBER
where status = 1 and NAME = #{name} and EMAIL = #{email}

SELECT count(*)
from MEMBER
where status = 1 and memberid = #{userid} and email = #{email}
