mapper.select:
SELECT RNO , IDX , MEMBERID , NICKNAME , NAME , EMAIL , BRITHDAY , TEL , GENDER , REGISTERDAY , GRADE , POINT , PICTURE , FINALLOGINDATE , FINALPWDATE FROM ( SELECT ROWNUM AS RNO , IDX , MEMBERID , NICKNAME , NAME , EMAIL , BRITHDAY , TEL , GENDER , REGISTERDAY , GRADE , POINT , PICTURE , FINALLOGINDATE , FINALPWDATE FROM MEMBER
WHERE STATUS = 1 AND LOWER(${searchType}) LIKE '%'||LOWER(#{searchWord})||'%'
ORDER BY ${range} DESC
ORDER BY IDX DESC
)V WHERE RNO BETWEEN #{startRno} AND #{endRno}

SELECT ${searchType}
FROM MEMBER
WHERE LOWER(${searchType}) LIKE '%'||LOWER(#{searchWord})||'%'

SELECT RNO , RESERVEID, PRICE, RESERVEDATE, MEMBERIDX, CHECKIN, CHECKOUT, PRODUCTNAME, WEEKPRICE, NAME, BUSINESSTEL FROM ( SELECT ROWNUM AS RNO , RESERVEID, PRICE, RESERVEDATE, MEMBERIDX, CHECKIN, CHECKOUT, PRODUCTNAME, WEEKPRICE, NAME, BUSINESSTEL FROM RESERVE1 A JOIN RESERVE2 B ON A.RESERVEID = B.FK_RESERVEID JOIN PRODUCT C ON A.FK_PRODUCTID = C.PRODUCTID JOIN LONTION D ON C.FK_LARGECATEGORYONTIONCODE = D.LARGECATEGORYONTIONCODE WHERE A.STATUS = 1
AND LOWER(${searchType}) LIKE '%'||LOWER(#{searchWord})||'%'
ORDER BY RESERVEID DESC )V WHERE RNO BETWEEN #{startRno} AND #{endRno}

SELECT RNO , FK_LARGECATEGORYCODE , NAME , BUSINESSTEL , ADDRESS , INFO , ONTIONTYPE , REGDAY FROM ( SELECT ROWNUM AS RNO , FK_LARGECATEGORYCODE , NAME , BUSINESSTEL , ADDRESS , INFO , ONTIONTYPE , REGDAY FROM LONTION
WHERE STATUS = 1 AND LOWER(${searchType}) LIKE '%'||LOWER(#{searchWord})||'%'
ORDER BY RNO DESC )V WHERE RNO BETWEEN #{startRno} AND #{endRno}

SELECT COUNT(*)
FROM INQUIRYBOARD
WHERE FK_MEMBER = #{memberid}

SELECT SEQ , FK_MEMBER , NAME , SUBJECT , CONTENT , PW , READCOUNT , TO_CHAR(REGDATE,'YYYY-MM-DD HH24:MI:SS') AS REGDATE , STATUS , DELETEDAY , COMMENTCOUNT , FILENAME , ORGFILENAME , FILESIZE FROM INQUIRYBOARD WHERE STATUS = 1 AND FK_MEMBER = #{memberid}
AND ${searchtype} LIKE '%'||#{searchword}||'%'
ORDER BY SEQ DESC
