mapper.select:
select count(*) from ( select a.week as weekPrice , a.weekend AS weekenPrice,b.img AS IMG,b.name AS name,b.address AS address,b.largeCategoryontionCode from ( select distinct fk_largecategoryontioncode,min(weekprice) as week,min(weekenprice) as weekend from ( SELECT fk_largecategoryontioncode,b.weekprice as weekprice,b.weekenprice as weekenprice, roomoption FROM ( SELECT FK_PRODUCTID,b.checkin,b.checkout FROM RESERVE1 A JOIN RESERVE2 B ON a.reserveid = B.FK_RESERVEID WHERE status=1
and (#{checkin_date} BETWEEN CHECKIN+1 AND CHECKOUT-1 OR #{checkout_date} BETWEEN CHECKIN+1 AND CHECKOUT-1 OR (#{checkin_date} < checkin+1 AND checkout-1 < #{checkout_date}))
) A RIGHT JOIN PRODUCT B ON a.fk_productid = B.PRODUCTID WHERE a.fk_productid IS NULL
and (weekprice between #{minPrice} and #{maxPrice} or weekenprice between #{minPrice} and #{maxPrice})
and to_number(substr(B.roomtype,3)) >=#{per}
and
roomoption like '%'||#{item}||'%'
) group by fk_largecategoryontioncode ) a join lontion b on a.fk_largecategoryontioncode=b.LARGECATEGORYONTIONCODE where b.status=1 and
b.ontiontype like '%'||#{item}||'%'
and
b.fk_largecategorycode like '%'||#{item}||'%'
and (b.address like '%'||#{searchWord}||'%' or b.name like '%'||#{searchWord}||'%')
and b.name like '%'||#{hotelName}||'%'
) a left join (select hotelidx,avg(star) as star,count(*) as starcnt from review group by hotelidx) b on a.largecategoryontioncode=b.hotelidx
where nvl(b.star,0) between #{minStar} and #{maxStar}

select.if:
select * from ( select ROW_NUMBER() OVER(ORDER BY a.name) NUM,a.*,b.star,b.starcnt from ( select a.week as weekPrice , a.weekend AS weekenPrice,b.img AS IMG,b.name AS name,b.address AS address,b.largeCategoryontionCode from ( select distinct fk_largecategoryontioncode,min(weekprice) as week,min(weekenprice) as weekend from ( SELECT fk_largecategoryontioncode,b.weekprice as weekprice,b.weekenprice as weekenprice, roomoption FROM ( SELECT FK_PRODUCTID,b.checkin,b.checkout FROM RESERVE1 A JOIN RESERVE2 B ON a.reserveid = B.FK_RESERVEID WHERE status=1
and (#{checkin_date} BETWEEN CHECKIN+1 AND CHECKOUT-1 OR #{checkout_date} BETWEEN CHECKIN+1 AND CHECKOUT-1 OR (#{checkin_date} < checkin+1 AND checkout-1 < #{checkout_date}))
) A RIGHT JOIN PRODUCT B ON a.fk_productid = B.PRODUCTID WHERE a.fk_productid IS NULL
and (weekprice between #{minPrice} and #{maxPrice} or weekenprice between #{minPrice} and #{maxPrice})
and
roomoption like '%'||#{item}||'%'
and to_number(substr(B.roomtype,3)) >=#{per}
) group by fk_largecategoryontioncode ) a join lontion b on a.fk_largecategoryontioncode=b.LARGECATEGORYONTIONCODE where b.status=1 and
b.ontiontype like '%'||#{item}||'%'
and
b.fk_largecategorycode like '%'||#{item}||'%'
and (b.address like '%'||#{searchWord}||'%' or b.name like '%'||#{searchWord}||'%')
and b.name like '%'||#{hotelName}||'%'
) a left join (select hotelidx,avg(star) as star,count(*) as starcnt from review group by hotelidx ) b on a.largecategoryontioncode=b.hotelidx
where nvl(b.star,0) between #{minStar} and #{maxStar}
) where num between #{startRno} and #{endRno}
order by ${sort}
order by largecategoryontioncode

select a.*,b.fk_memberid as regDay from ( select * from ( select ROW_NUMBER() OVER(ORDER BY a.name) NUM,a.*,b.star as star,b.starcnt as starcnt from ( select a.week as weekPrice , a.weekend AS weekenPrice,b.img AS IMG,b.name AS name,b.address AS address,b.largeCategoryontionCode from ( select distinct fk_largecategoryontioncode,min(weekprice) as week,min(weekenprice) as weekend from ( SELECT fk_largecategoryontioncode,b.weekprice as weekprice,b.weekenprice as weekenprice,roomoption FROM ( SELECT FK_PRODUCTID,b.checkin,b.checkout FROM RESERVE1 A JOIN RESERVE2 B ON a.reserveid = B.FK_RESERVEID WHERE status=1
and (#{checkin_date} BETWEEN CHECKIN+1 AND CHECKOUT-1 OR #{checkout_date} BETWEEN CHECKIN+1 AND CHECKOUT-1 OR (#{checkin_date} < checkin+1 AND checkout-1 < #{checkout_date}))
) A RIGHT JOIN PRODUCT B ON a.fk_productid = B.PRODUCTID WHERE a.fk_productid IS NULL
and (weekprice between #{minPrice} and #{maxPrice} or weekenprice between #{minPrice} and #{maxPrice})
and to_number(substr(B.roomtype,3)) >=#{per}
and
roomoption like '%'||#{item}||'%'
) group by fk_largecategoryontioncode ) a join lontion b on a.fk_largecategoryontioncode=b.LARGECATEGORYONTIONCODE where b.status=1 and
b.ontiontype like '%'||#{item}||'%'
and
b.fk_largecategorycode like '%'||#{item}||'%'
and (b.address like '%'||#{searchWord}||'%' or b.name like '%'||#{searchWord}||'%')
and b.name like '%'||#{hotelName}||'%'
) a left join (select hotelidx,avg(star) as star,count(*) as starcnt from review group by hotelidx) b on a.largecategoryontioncode=b.hotelidx
where nvl(b.star,0) between #{minStar} and #{maxStar}
) where num between #{startRno} and #{endRno} ) a left join ( select * from likelist where fk_memberid=#{memberid} and status=1) b on a.largecategoryontioncode = b.fk_hotelid
order by ${sort}
order by largecategoryontioncode

mapper.select:
select count(*)
from lontion a join (
					 select fk_hotelid from likelist where status=1 and fk_memberid=#{memberid}
					 ) b
on a.largeCategoryontionCode=b.fk_hotelid

select * from
(
select ROW_NUMBER() OVER(ORDER BY b.likelistid) NUM,address, name, img, largeCategoryontionCode, fk_hotelid as fk_LargeCategoryCode
from lontion a join (
					 select likelistid,fk_hotelid from likelist where status=1 and fk_memberid=#{memberid}
					 ) b
on a.largeCategoryontionCode=b.fk_hotelid
)  
where num between #{startRno} and #{endRno}

select distinct col as lontion
from(
	Select Regexp_Substr((select ontiontype from (select listagg(ontiontype||',')within group (order by ontiontype) as ontiontype
	from (select 1,ontiontype from lontion)
	group by 1)),'[^,]+', 1, Level) COL From Dual
	Connect By Regexp_Substr((select ontiontype from (select listagg(ontiontype||',')within group (order by ontiontype) as ontiontype
	from (select 1,ontiontype from lontion)
	group by 1)), '[^,]+', 1, Level) Is Not Null
)

select distinct col as pontion
from(
	Select Regexp_Substr((select roomoption from (select listagg(roomoption||',')within group (order by roomoption) as roomoption
	from (select 1,roomoption from product)
	group by 1)),'[^,]+', 1, Level) COL From Dual
	Connect By Regexp_Substr((select roomoption from (select listagg(roomoption||',')within group (order by roomoption) as roomoption
	from (select 1,roomoption from product)
	group by 1)), '[^,]+', 1, Level) Is Not Null
)

select nickname as name,picture as img,productName,title,content,star,fileName,regdate as reserveDate from ( select roomidx,title,content,star,regdate,filename,nickname,picture from ( select memberidx,roomidx,title,content,imgidx,star,regdate,filename from ( select reviewidx,memberidx,roomidx,title,content,imgidx,star,regdate from review where status=1
and hotelidx=#{hotelidx}
order by ${sort}
)a left join ( select reviewidx, listagg(filename,',') within group(order by reviewidx) as filename from review_img group by reviewidx )b on a.reviewidx=b.reviewidx )a join ( select idx,nickname,picture from member where status=1 )b on a.memberidx=b.idx )a join ( select productid,productname from product where productstatus=1 )b on a.roomidx =b.productid

select count(*) from ( select roomidx,title,content,star,regdate,filename,nickname,picture from ( select memberidx,roomidx,title,content,imgidx,star,regdate,filename from ( select reviewidx,memberidx,roomidx,title,content,imgidx,star,regdate from review where status=1
and hotelidx=#{hotelidx}
order by ${sort}
)a left join ( select reviewidx, listagg(filename,',') within group(order by reviewidx) as filename from review_img group by reviewidx )b on a.reviewidx=b.reviewidx )a join ( select idx,nickname,picture from member where status=1 )b on a.memberidx=b.idx )a join ( select productid,productname from product where productstatus=1 )b on a.roomidx =b.productid
