mapper.select:
select count(*) from ( select a.week as weekPrice , a.weekend AS weekenPrice,b.img AS IMG,b.name AS name,b.address AS address,b.largeCategoryontionCode from ( select distinct fk_largecategoryontioncode,min(weekprice) as week,min(weekenprice) as weekend from ( SELECT fk_largecategoryontioncode,b.weekprice as weekprice,b.weekenprice as weekenprice FROM ( SELECT FK_PRODUCTID,b.checkin,b.checkout FROM RESERVE1 A JOIN RESERVE2 B ON a.reserveid = B.FK_RESERVEID
WHERE #{checkin_date} BETWEEN CHECKIN+1 AND CHECKOUT-1 OR #{checkout_date} BETWEEN CHECKIN+1 AND CHECKOUT-1
) A RIGHT JOIN PRODUCT B ON a.fk_productid = B.PRODUCTID WHERE a.fk_productid IS NULL
and substr(roomtype,3)=#{per}
) group by fk_largecategoryontioncode ) a join lontion b on a.fk_largecategoryontioncode=b.LARGECATEGORYONTIONCODE
where b.address like '%'||#{searchWord}||'%' or b.name like '%'||#{searchWord}||'%'
) a left join (select hotelidx,avg(star) as star,count(*) as starcnt from review group by hotelidx) b on a.largecategoryontioncode=b.hotelidx

select * from ( select ROW_NUMBER() OVER(ORDER BY a.name) NUM,a.*,b.star as star,b.starcnt as starcnt from ( select a.week as weekPrice , a.weekend AS weekenPrice,b.img AS IMG,b.name AS name,b.address AS address,b.largeCategoryontionCode from ( select distinct fk_largecategoryontioncode,min(weekprice) as week,min(weekenprice) as weekend from ( SELECT fk_largecategoryontioncode,b.weekprice as weekprice,b.weekenprice as weekenprice FROM ( SELECT FK_PRODUCTID,b.checkin,b.checkout FROM RESERVE1 A JOIN RESERVE2 B ON a.reserveid = B.FK_RESERVEID
WHERE #{checkin_date} BETWEEN CHECKIN+1 AND CHECKOUT-1 OR #{checkout_date} BETWEEN CHECKIN+1 AND CHECKOUT-1
) A RIGHT JOIN PRODUCT B ON a.fk_productid = B.PRODUCTID WHERE a.fk_productid IS NULL
and substr(roomtype,3)=#{per}
) group by fk_largecategoryontioncode ) a join lontion b on a.fk_largecategoryontioncode=b.LARGECATEGORYONTIONCODE
where b.address like '%'||#{searchWord}||'%' or b.name like '%'||#{searchWord}||'%'
) a left join (select hotelidx,avg(star) as star,count(*) as starcnt from review group by hotelidx) b on a.largecategoryontioncode=b.hotelidx ) where num between #{startRno} and #{endRno}

select.if:
select a.*,b.star as star,b.starcnt as starcnt from ( select a.week as weekPrice , a.weekend AS weekenPrice,b.img AS IMG,b.name AS name,b.address AS address,b.largeCategoryontionCode from ( select distinct fk_largecategoryontioncode,min(weekprice) as week,min(weekenprice) as weekend from ( SELECT fk_largecategoryontioncode,b.weekprice as weekprice,b.weekenprice as weekenprice FROM ( SELECT FK_PRODUCTID,b.checkin,b.checkout FROM RESERVE1 A JOIN RESERVE2 B ON a.reserveid = B.FK_RESERVEID
WHERE #{CHECKIN} BETWEEN CHECKIN+1 AND CHECKOUT-1 OR #{CHECKOUT} BETWEEN CHECKIN+1 AND CHECKOUT-1
) A RIGHT JOIN PRODUCT B ON a.fk_productid = B.PRODUCTID WHERE a.fk_productid IS NULL
and substr(roomtype,3)=#{per}
) group by fk_largecategoryontioncode ) a join lontion b on a.fk_largecategoryontioncode=b.LARGECATEGORYONTIONCODE
where b.address like '%'||#{searchWord}||'%' or b.name like '%'||#{searchWord}||'%'
) a left join (select hotelidx,avg(star) as star,count(*) as starcnt from review group by hotelidx) b on a.largecategoryontioncode=b.hotelidx

select a.*,b.fk_memberid as regDay from ( select a.*,b.star as star,b.starcnt as starcnt from ( select a.week as weekPrice , a.weekend AS weekenPrice,b.img AS IMG,b.name AS name,b.address AS address,b.largeCategoryontionCode from ( select distinct fk_largecategoryontioncode,min(weekprice) as week,min(weekenprice) as weekend from ( SELECT fk_largecategoryontioncode,b.weekprice as weekprice,b.weekenprice as weekenprice FROM ( SELECT FK_PRODUCTID,b.checkin,b.checkout FROM RESERVE1 A JOIN RESERVE2 B ON a.reserveid = B.FK_RESERVEID
WHERE #{CHECKIN} BETWEEN CHECKIN+1 AND CHECKOUT-1 OR #{CHECKOUT} BETWEEN CHECKIN+1 AND CHECKOUT-1
) A RIGHT JOIN PRODUCT B ON a.fk_productid = B.PRODUCTID WHERE a.fk_productid IS NULL
and substr(roomtype,3)=#{per}
) group by fk_largecategoryontioncode ) a join lontion b on a.fk_largecategoryontioncode=b.LARGECATEGORYONTIONCODE
where b.address like '%'||#{searchWord}||'%' or b.name like '%'||#{searchWord}||'%'
) a left join (select hotelidx,avg(star) as star,count(*) as starcnt from review group by hotelidx) b on a.largecategoryontioncode=b.hotelidx ) a left join ( select * from likelist where fk_memberid=#{memberid} and status=1) b on a.largecategoryontioncode = b.fk_hotelid

mapper.select:
select address, name, img, largeCategoryontionCode, fk_hotelid as fk_LargeCategoryCode
from lontion a join (
					 select fk_hotelid from likelist where status=1 and fk_memberid=#{memberId}
					 ) b
on a.largeCategoryontionCode=b.fk_hotelid

SELECT fk_hotelid
FROM LIKELIST
WHERE STATUS=1 AND fk_memberid=#{memberId}
